const state = await snap.request({
  method: "snap_getInterfaceState",
  params: {
    id: interfaceId,
  },
});

{
  "initialPermissions": {
    "snap_manageState": {}
  }
}

const state = await snap.request({
  method: "snap_getState",
  params: {
    key: "some.nested.value", // Optional, defaults to entire state
    encrypted: true, // Optional, defaults to `true`
  },
});

{
  "initialPermissions": {
    "snap_manageState": {}
  }
}

// Persist some data.
await snap.request({
  method: "snap_manageState",
  params: {
    operation: "update",
    newState: { hello: "world" },
  },
});

// At a later time, get the stored data.
const persistedData = await snap.request({
  method: "snap_manageState",
  params: { operation: "get" },
});

console.log(persistedData);
// { hello: 'world' }

// If there's no need to store data anymore, clear it out.
await snap.request({
  method: "snap_manageState",
  params: {
    operation: "clear",
  },
});

{
  "initialPermissions": {
    "endowment:network-access": {}
